import React from "react";
import "./styles/ShopReask.css";
import { products } from "./component/ProductList";
import { useParams, useNavigate } from "react-router-dom";
import ProductItem2 from "./component/ProductItem2";

function ShopReask() {
  const { id } = useParams();
  const navigate = useNavigate();
  const product = products.find((product) => product.id === parseInt(id));

  if (!product) {
    return <div>상품을 찾을 수 없습니다.</div>;
  }

  const handleBuyClick = () => {
    navigate(`/barcode/${product.id}`);
  };

  return (
    <div className="ShopReask">
      <ProductItem2 product={product} />
      <button className="buy-button" onClick={handleBuyClick}>
        구매하기
      </button>
    </div>
  );
}

export default ShopReask;
