import React, { useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const LoginCallback = ({ onLoginSuccess }) => {
    const location = useLocation();

    useEffect(() => {
        // URL에서 인증 코드를 추출합니다.
        const code = new URLSearchParams(location.search).get('code');
        if (code) {
            axios.get(`/login/oauth2/code/kakao?code=${code}`, { withCredentials: true })
                .then(response => {
                    console.log('GET 요청 성공:', response.data);
                    dispatch({ type: 'SET_ID', payload: response.data.id });
                    if (onLoginSuccess) {
                        onLoginSuccess(response.data.id); // 콜백 함수 호출
                    }
                })
                .catch(error => {
                    console.error('GET 요청 실패:', error);
                });
        }
    }, [location.search, dispatch, onLoginSuccess]);

    return (
        <div>
            로그인 처리 중...
        </div>
    );
};

export default LoginCallback;
