import React, { useEffect } from "react";
import axios from "axios";
import "./styles/LoginSlider.css";
import { ReactComponent as IconKakao } from "./assets/카카오로그인.svg";
import { useGlobalState } from "./GlobalState"; // GlobalState에서 custom hook을 가져옵니다.
import { useNavigate } from "react-router-dom"; // useNavigate 훅을 가져옵니다.

function LoginSlider({ slides = [] }) {
    const { dispatch } = useGlobalState(); // 전역 상태의 dispatch를 가져옵니다.
    const navigate = useNavigate(); // useNavigate 훅을 초기화합니다.

    useEffect(() => {
        // URL에서 authorization code 추출
        const urlParams = new URLSearchParams(window.location.search);
        const code = urlParams.get('code');
        if (code) {
            // 서버로 authorization code를 보내고 응답을 받음
            axios.post('http://localhost:8080/login/oauth2/code/kakao', { code })
                .then(response => {
                    console.log('Server Response:', response.data);
                    // 응답 데이터를 상태로 저장하거나 원하는 로직 추가
                    dispatch({ type: 'SET_USER', payload: response.data });
                    // 로그인 성공 후 특정 페이지로 리다이렉트
                    navigate('/myinfo');
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        }
    }, [dispatch, navigate]);

    const handleKakaoLoginClick = () => {
        window.location.href = 'https://kauth.kakao.com/oauth/authorize?response_type=code&client_id=4fd07ea2b22cd1efe588cb88ceb5763c&redirect_uri=http://localhost:8080/login/oauth2/code/kakao';
    };

    return (
        <div className="screen">
            <div className="kakao-login-button">
                <div className="kakao-login-content">
                    <IconKakao className="kakao-login-icon" alt="Kakao Icon" />
                    <div className="kakao-login-text" onClick={handleKakaoLoginClick}>
                        카카오톡 로그인하기
                    </div>
                </div>
            </div>
        </div>
    );
}

export default LoginSlider;
