import React from "react";
import { Link } from "react-router-dom";
import "../styles/ProductItem.css";
import HeaderShop from "../HeaderShop";
import BottomBar from "../BottomBar";

const ProductItem = ({ product }) => {
  const currentPoints = 3500; // 현재 포인트 (예시)
  const remainingPoints = currentPoints - product.points;

  return (
    <>
      <div className="top-container">
        <HeaderShop />
        <div className="point-container">
          <div className="point-text">보유 포인트:3500P</div>
        </div>

      </div>
      <div className="screen">
        <div className="ProductItem">
          <div className="product-card-box">
            <div className="product-card-container">
              <div className="product-card-text"> {product.name}</div>
            </div>
          </div>

          <div className="product-img-container">
            <img
              className="product-styled-image"
              src={product.image}
              alt={product.name}
            />
          </div>

          <div className="text-content-box">
            <div className="text-content-container">
              <p className="text-content">{product.description}</p>
            </div>
          </div>

          <div className="gift-icon-box">
            <div className="gift-icon-container">
              <div className="gift-icon-text">기프티콘(CU)</div>
              <div className="gift-icon-points">{product.points}P</div>
            </div>
          </div>

          <div className="product-button-box">
            <div className="product-button-container">
              <div className="product-button-text">구매하기</div>
            </div>
          </div>
        </div>
      </div>
      <BottomBar />
    </>
  );
};

export default ProductItem;
