import React from "react";
import axios from "axios";
import "./styles/LoginSlider.css";
import { ReactComponent as Image12 } from "./assets/image 12.svg";
import { ReactComponent as IconKakako } from "./assets/카카오로그인.svg";
import { useGlobalState } from "./GlobalState"; // GlobalState에서 custom hook을 가져옵니다.

function LoginSlider({ slides = [] }) {
    const { dispatch } = useGlobalState(); // 전역 상태의 dispatch를 가져옵니다.
    /*
    const handleKakaoLoginClick = () => {
        axios.get('http://localhost:8080/login',{ withCredentials: true }) // 원하는 API 엔드포인트를 입력하세요.
            .then(response => {
                console.log('GET 요청 성공:', response.data);
                dispatch({ type: 'SET_ID', payload: response.data.id }); // 'SET_USER_ID'를 'SET_ID'로 변경
            })
            .catch(error => {
                console.error('GET 요청 실패:', error);
            });
    };
    */

    const handleKakaoLoginClick = () => {
        window.location.href = 'https://kauth.kakao.com/oauth/authorize?response_type=code&client_id=4fd07ea2b22cd1efe588cb88ceb5763c&redirect_uri=http://localhost:8080/login/oauth2/code/kakao';
    };


    return (
        <div className="screen">
            <div className="kakao-login-button">
                <div className="kakao-login-content">
                    <IconKakako className="kakao-login-icon" alt="Kakao Icon" />
                    <div className="kakao-login-text" onClick={handleKakaoLoginClick}>
                        카카오톡 로그인하기
                        console.log("123412412"); // JSON 데이터를 콘솔에 출력
                    </div>
                </div>
            </div>
        </div>
    );
}

export default LoginSlider;
